INSERT INTO `testcase` (`id`, `Module_name`, `description`, `precondition`, `test_steps`, `expected_results`, `bug_type`, `Product_name`, `Version`, `created_at`, `device_name`, `android_version`, `file_attachment`, `tested_by_id`, `tested_by_name`, `tested_at`, `actual_result`, `testing_result`, `preconditions`, `result_changed_at`, `cleared_flag`, `cleared_by`) VALUES
('NTC_000000001', 'TC_PurchaseApproval_01', 'Verify display of purchased products list', NULL, 'Launch the app and view the purchased product list.', 'The list should display all purchased products (milk and non-milk) with their respective details.', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', 'iphone', '14`', '', 0, 'NARMADHADEVI S', '2025-03-25 11:36:07', '', 'Pass', 'Purchased products exist in the database.', NULL, 0, NULL),
('NTC_000000002', 'TC_PurchaseApproval_02', 'Verify details of milk product items', NULL, 'View the details of a milk product in the list.', 'Milk product details (e.g., supplier, milk type, qty, can type) should be displayed correctly.', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Milk products exist in the database.', NULL, 0, NULL),
('NTC_000000003', 'TC_PurchaseApproval_03', 'Verify details of non-milk product items', NULL, 'View the details of a non-milk product in the list.', 'Non-milk product details (e.g., supplier, product name, qty, rate, price) should be displayed correctly.', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Non-milk products exist in the database.', NULL, 0, NULL),
('NTC_000000004', 'TC_PurchaseApproval_04', 'Verify approve button for milk products', NULL, 'Tap the \"Approve\" button for a milk product.', 'The product status should update to \"Approved,\" and the button should become disabled or change to \"Approved.\"', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Milk products exist in the database.', NULL, 0, NULL),
('NTC_000000005', 'TC_PurchaseApproval_05', 'Verify approve button for non-milk products', NULL, 'Tap the \"Approve\" button for a non-milk product.', 'The product status should update to \"Approved,\" and the button should become disabled or change to \"Approved.\"', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Non-milk products exist in the database.', NULL, 0, NULL),
('NTC_000000006', 'TC_PurchaseApproval_06', 'Verify approval of multiple products', NULL, 'Approve multiple items from the list.', 'All selected items should update to \"Approved\" status.', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Both milk and non-milk products exist.', NULL, 0, NULL),
('NTC_000000007', 'TC_PurchaseApproval_07', 'Handle empty product list', NULL, 'Launch the app with no products in the list.', 'The app should display a message, e.g., \"No products available.\"', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'No purchased products exist in the database.', NULL, 0, NULL),
('NTC_000000008', 'TC_PurchaseApproval_08', 'Verify status update after approval', NULL, 'Approve a product and verify its status in the database or UI.', 'The product status should update to \"Approved\" in both the UI and the backend database.', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Purchased products are pending approval.', NULL, 0, NULL),
('NTC_000000009', 'TC_PurchaseApproval_09', 'Handle duplicate products', NULL, 'View the product list with duplicates.', 'The app should either consolidate duplicates or display them separately without errors.', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Duplicate products exist in the database.', NULL, 0, NULL),
('NTC_000000010', 'TC_PurchaseApproval_10', 'Validate approve button disablement on approval', NULL, 'Approve an item and check the button state.', 'The approve button should become disabled or change to indicate the product is approved.', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Product is already approved.', NULL, 0, NULL),
('NTC_000000011', 'TC_PurchaseApproval_11', 'Verify layout switching between product types', NULL, 'Scroll through the product list and verify layouts for milk and non-milk products.', 'Layouts should switch appropriately based on the product type.', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Both milk and non-milk products exist.', NULL, 0, NULL),
('NTC_000000012', 'TC_PurchaseApproval_12', 'Verify performance with a large product list', NULL, 'Load a large list of milk and non-milk products and scroll through the RecyclerView.', 'The app should handle the large list smoothly without performance issues.', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'A large number of products exist in the database.', NULL, 0, NULL),
('NTC_000000013', 'TC_PurchaseApproval_13', 'Handle network issues during approval', NULL, 'Tap the \"Approve\" button during a network issue.', 'The app should display an appropriate error message and allow retrying the operation.', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Network connectivity is unstable.', NULL, 0, NULL),
('NTC_000000014', 'TC_PurchaseApproval_14', 'Validate error handling for failed approvals', NULL, 'Tap the \"Approve\" button for any product.', 'The app should display an error message, e.g., \"Failed to approve. Please try again later.\"', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Backend service is down.', NULL, 0, NULL),
('NTC_000000015', 'TC_PurchaseApproval_15', 'Verify persistence of approved status', NULL, 'Approve a product, restart the app, and check the status.', 'The \"Approved\" status should persist after restarting the app.', NULL, 'ProcuraAdmin', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Products have been approved.', NULL, 0, NULL),
('NTC_000000016', 'TC_Login_01', 'Validate Empty Mobile Number, Email, and Password', NULL, '1. Leave Mobile Number, Mail ID, and Password fields empty. \n 2. Tap the Login button.', 'Error message for each field indicating they cannot be empty (e.g., \"Mobile Number cannot be empty\", \"Mail ID cannot be empty\", \"Password cannot be empty\").', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000017', 'TC_Login_02', 'Validate Empty Mobile Number with Filled Email and Password', NULL, '1. Leave Mobile Number field empty. \n 2. Enter valid Mail ID and Password. \n 3. Tap the Login button.', 'Error message for Mobile Number (e.g., \"Mobile Number cannot be empty\").', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000018', 'TC_Login_03', 'Validate Empty Mail ID with Filled Mobile Number and Password', NULL, '1. Leave Mail ID field empty. \n 2. Enter valid Mobile Number and Password. \n 3. Tap the Login button.', 'Error message for Mail ID (e.g., \"Mail ID cannot be empty\").', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000019', 'TC_Login_04', 'Validate Empty Password with Filled Mobile Number and Mail ID', NULL, '1. Leave Password field empty. \n 2. Enter valid Mobile Number and Mail ID. \n 3. Tap the Login button.', 'Error message for Password (e.g., \"Password cannot be empty\").', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000020', 'TC_Login_05', 'Validate Invalid Mobile Number Format', NULL, '1. Enter an invalid Mobile Number (e.g., too short or too long). \n 2. Enter valid Mail ID and Password. \n 3. Tap the Login button.', 'Error message for invalid Mobile Number (e.g., \"Invalid mobile number format\").', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000021', 'TC_Login_06', 'Validate Invalid Mail ID Format', NULL, '1. Enter valid Mobile Number. \n 2. Enter invalid Mail ID (e.g., \"invalidemail.com\"). \n 3. Enter valid Password. \n 4. Tap the Login button.', 'Error message for invalid Mail ID format (e.g., \"Invalid email format\").', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000022', 'TC_Login_07', 'Validate Invalid Password', NULL, '1. Enter valid Mobile Number or Mail ID. \n 2. Enter invalid Password. \n 3. Tap the Login button.', 'Error message indicating incorrect Password (e.g., \"Incorrect password\").', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000023', 'TC_Login_08', 'Validate Successful Login', NULL, '1. Enter valid Mobile Number (or Mail ID). \n 2. Enter correct Password. \n 3. Tap the Login button.', 'User is logged in successfully and redirected to the home screen/dashboard.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000024', 'TC_Login_09', 'Validate Login with Mobile Number and Password', NULL, '1. Select Mobile Number option from Mail ID selection TextView. \n 2. Enter valid Mobile Number. \n 3. Enter valid Password. \n 4. Tap the Login button.', 'User is logged in successfully and redirected to the home screen/dashboard.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000025', 'TC_Login_10', 'Validate Login with Mail ID and Password', NULL, '1. Select Mail ID option from Mail ID selection TextView. \n 2. Enter valid Mail ID. \n 3. Enter valid Password. \n 4. Tap the Login button.', 'User is logged in successfully and redirected to the home screen/dashboard.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000026', 'TC_Login_11', 'Validate \'Forgot Password\' Link (If Applicable)', NULL, '1. Tap the \"Forgot Password\" link.', 'App navigates to password reset screen where the user can request a password reset.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000027', 'TC_Login_12', 'Validate \'Login\' Button Disabled Initially', NULL, '1. Do not enter any details in the Mobile Number, Mail ID, and Password fields.', 'Login button is disabled until valid input is provided.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000028', 'TC_Login_13', 'Validate \'Login\' Button Enabled after Valid Input', NULL, '1. Enter valid Mobile Number (or Mail ID). \n 2. Enter valid Password.', 'Login button becomes enabled and allows the user to tap it.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000029', 'TC_Login_14', 'Validate Login Behavior with Social Media (If Applicable)', NULL, '1. Tap login option for social media account (e.g., Google). \n 2. Complete authentication process.', 'User is logged in using social media account and redirected to the home page.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App has social media login options', NULL, 0, NULL),
('NTC_000000030', 'TC_Login_15', 'Validate Session Expiry After Logout', NULL, '1. Remain idle for a specified time (e.g., 15 minutes). \n 2. Attempt to perform any action.', 'User is logged out automatically and redirected to login screen.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is logged in, session is active', NULL, 0, NULL),
('NTC_000000031', 'TC_Home_01', 'Validate Home Page Load', NULL, '1. Open the app and navigate to the home page.', 'The home page should display both buttons: \"Summary\" and \"History\"', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The app is launched, and the user is on the home page.', NULL, 0, NULL),
('NTC_000000032', 'TC_Home_02', 'Validate Purchase Button Functionality', NULL, '1. Tap the \"Summary\" button.', 'The app should navigate to the Purchase summary screen or Checkout page.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The user is on the home page with both buttons visible.', NULL, 0, NULL),
('NTC_000000033', 'TC_Home_03', 'Validate Purchase Summary Button Functionality', NULL, '1. Tap the \"Purchase History\" button.', 'The app should navigate to the Purchase History screen, displaying relevant purchase details.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The user is on the home page with both buttons visible.', NULL, 0, NULL),
('NTC_000000034', 'TC_Home_04', 'Validate Purchase Button Disabled (If Applicable)', NULL, '1. Ensure the user is not logged in or there are no items in the cart. \n 2. Check the \"Purchase\" button\'s state.', 'The \"Purchase\" button should be disabled or show a message like \"Please log in\" or \"Your cart is empty.\"', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is not logged in or cart is empty.', NULL, 0, NULL),
('NTC_000000035', 'TC_Home_05', 'Validate Purchase Summary Button Disabled (If Applicable)', NULL, '1. Ensure the user has no purchase history. \n 2. Check the \"Purchase Summary\" button\'s state.', 'The \"Purchase Summary\" button should be disabled or show a message like \"No purchase history available.\"', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User has no purchase history.', NULL, 0, NULL),
('NTC_000000036', 'TC_Home_06', 'Validate Purchase Button with Valid Cart Items', NULL, '1. Add items to the cart (if applicable). \n 2. Tap the \"Purchase\" button.', 'The app should navigate to the Purchase or Checkout screen, showing the cart details.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The user is on the home page with items in the cart.', NULL, 0, NULL),
('NTC_000000037', 'TC_Home_07', 'Validate Purchase Summary Button with Previous Purchases', NULL, '1. Tap the \"Purchase Summary\" button.', 'The app should navigate to the Purchase Summary screen and show the user\'s purchase summary', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The user has made previous purchases.', NULL, 0, NULL),
('NTC_000000038', 'TC_Home_08', 'Validate UI and Layout for Buttons', NULL, '1. Open the app on different screen sizes and resolutions (e.g., phone, tablet). \n 2. Check the positioning of the \"Summary\" and \"History\" buttons.', 'Both buttons should be properly aligned and accessible across different screen sizes.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The app is launched, and the user is on the home page.', NULL, 0, NULL),
('NTC_000000039', 'TC_Home_09', 'Validate Button Visibility on Home Page (Portrait and Landscape)', NULL, '1. Open the app in portrait mode. \n 2. Rotate the device to landscape mode. \n 3. Ensure both buttons are visible and accessible in both orientations.', 'Both buttons should be visible and properly laid out in both portrait and landscape orientations.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The app is launched, and the user is on the home page.', NULL, 0, NULL),
('NTC_000000040', 'TC_Home_10', 'Validate Navigation Flow from \"Purchase\" Button', NULL, '1. Ensure that items are in the cart. \n 2. Tap the \"Purchase\" button.', 'The user should be redirected to the Checkout page or Purchase screen, showing cart details.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The user is on the home page with valid items in the cart.', NULL, 0, NULL),
('NTC_000000041', 'TC_Home_11', 'Validate Navigation Flow from \"Purchase Summary\" Button', NULL, '1. Tap the \"Purchase Summary\" button.', 'The app should navigate to the Purchase Summary page, displaying details of past purchases.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The user has made at least one purchase.', NULL, 0, NULL),
('NTC_000000042', 'TC_Home_12', 'Validate Button Accessibility for Screen Readers', NULL, '1. Navigate to the home page with accessibility features enabled. \n 2. Use a screen reader to check the button labels.', 'The buttons should be properly labeled with appropriate text, such as \"Purchase\" and \"View Purchase Summary.\"', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User has accessibility features enabled (e.g., screen reader, voice assistant).', NULL, 0, NULL),
('NTC_000000043', 'TC_Home_13', 'Validate Button Response Time', NULL, '1. Tap the \"Purchase\" button and observe the response time. \n 2. Tap the \"Purchase Summary\" button and observe the response time.', 'Both buttons should respond with minimal delay (e.g., less than 1 second).', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The app is launched, and the user is on the home page.', NULL, 0, NULL),
('NTC_000000044', 'TC_Home_14', 'Validate Button State After Successful Purchase', NULL, '1. Complete a purchase. \n 2. Return to the home page. \n 3. Check the state of both the \"Purchase\" and \"Purchase Summary\" buttons.', 'The \"Purchase\" button should either be disabled or show a message indicating that the user has already completed a purchase. The \"Purchase Summary\" button should navigate to the summary of that purchase.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The user has completed a successful purchase.', NULL, 0, NULL),
('NTC_000000045', 'TC_Home_15', 'Validate Button Press Effects (e.g., Visual Feedback)', NULL, '1. Tap the \"Purchase\" button and observe the visual effect. \n 2. Tap the \"Purchase Summary\" button and observe the visual effect.', 'Both buttons should provide visual feedback when pressed, such as a color change or ripple effect, to indicate that the tap was recognized.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'The app is launched, and the user is on the home page.', NULL, 0, NULL),
('NTC_000000046', 'TC_Purchase_01', 'Validate Layout Display Based on Milk Product Selection', NULL, '1. Select the \"Milk Product\" option in the Product Selection field.', 'The layout should display fields like Product Type, Milk Can Type, and Milk Can Weight.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page.', NULL, 0, NULL),
('NTC_000000047', 'TC_Purchase_02', 'Validate Layout Display Based on Grocery Product Selection', NULL, '1. Select the \"Grocery Product\" option in the Product Selection field.', 'The layout should display fields like Product Type, Product Weight, Purchase Rate per KG, and Total Purchase Amount.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page.', NULL, 0, NULL),
('NTC_000000048', 'TC_Purchase_03', 'Validate Milk Product Selection with Valid Data', NULL, '1. Select \"Milk Product\" from the Product Selection field. \n 2. Enter valid data for Product Type, Milk Can Type, and Milk Can Weight. \n 3. Tap the \"Save\" button.', 'The data should be saved and uploaded to the server. The layout should allow the user to upload product images.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page with Milk Product selected.', NULL, 0, NULL),
('NTC_000000049', 'TC_Purchase_04', 'Validate Grocery Product Selection with Valid Data', NULL, '1. Select \"Grocery Product\" from the Product Selection field. \n 2. Enter valid data for Product Type, Product Weight, Purchase Rate per KG, and Total Purchase Amount. \n 3. Tap the \"Save\" button.', 'The data should be saved and uploaded to the server. The layout should allow the user to upload product images.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page with Grocery Product selected.', NULL, 0, NULL),
('NTC_000000050', 'TC_Purchase_05', 'Validate Save Button Behavior', NULL, '1. Fill in data for either Milk Product or Grocery Product fields. \n 2. Tap the \"Save\" button.', 'The data should be uploaded to the server.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page, with data filled in relevant fields.', NULL, 0, NULL),
('NTC_000000051', 'TC_Purchase_06', 'Validate Image Upload Functionality', NULL, '1. Tap one of the three image upload buttons below the \"Save\" button. \n 2. Select an image from the gallery or camera.', 'The selected image should be uploaded successfully for the product.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page with data saved.', NULL, 0, NULL),
('NTC_000000052', 'TC_Purchase_07', 'Validate Multiple Image Uploads', NULL, '1. Tap all three image upload buttons. \n 2. Select an image for each button.', 'All three images should be uploaded successfully, corresponding to the fields.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page with data saved.', NULL, 0, NULL),
('NTC_000000053', 'TC_Purchase_08', 'Validate Navigation to Summary Page', NULL, '1. Tap the \"Summary\" button at the bottom of the page.', 'The app should navigate to the Purchase Summary page.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page.', NULL, 0, NULL),
('NTC_000000054', 'TC_Purchase_09', 'Validate Navigation to History Page', NULL, '1. Tap the \"History\" button at the bottom of the page.', 'The app should navigate to the Purchase History page.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page.', NULL, 0, NULL),
('NTC_000000055', 'TC_Purchase_10', 'Validate Save Button Disabled (If Fields Are Empty)', NULL, '1. Ensure all fields are empty (for Milk or Grocery products). \n 2. Check the state of the \"Save\" button.', 'The \"Save\" button should be disabled or not clickable.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page, with no data filled.', NULL, 0, NULL),
('NTC_000000056', 'TC_Purchase_11', 'Validate Save Button Enabled (After Valid Data Entry)', NULL, '1. Fill in all required fields with valid data for either Milk or Grocery products.', 'The \"Save\" button should be enabled and clickable.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page, with valid data filled.', NULL, 0, NULL),
('NTC_000000057', 'TC_Purchase_12', 'Validate Image Upload Button Disabled (If No Product Selected)', NULL, '1. Ensure no product (Milk or Grocery) is selected. \n 2. Check the state of the image upload buttons.', 'The image upload buttons should be disabled.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page, with no product selected.', NULL, 0, NULL),
('NTC_000000058', 'TC_Purchase_13', 'Validate Image Upload Button Enabled (After Product Selection)', NULL, '1. Select a product (Milk or Grocery). \n 2. Check the state of the image upload buttons.', 'The image upload buttons should be enabled once the product is selected.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page, with a product selected.', NULL, 0, NULL),
('NTC_000000059', 'TC_Purchase_14', 'Validate Data Upload to Server for Milk Products', NULL, '1. Select \"Milk Product\" in the Product Selection field. \n 2. Enter valid data for Milk Can Type and Milk Can Weight. \n 3. Tap the \"Save\" button.', 'The entered data should be uploaded to the server successfully.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page with Milk Product selected.', NULL, 0, NULL),
('NTC_000000060', 'TC_Purchase_15', 'Validate Data Upload to Server for Grocery Products', NULL, '1. Select \"Grocery Product\" in the Product Selection field. \n 2. Enter valid data for Product Type, Product Weight, and Purchase Rate per KG. \n 3. Tap the \"Save\" button.', 'The entered data should be uploaded to the server successfully.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page with Grocery Product selected.', NULL, 0, NULL),
('NTC_000000061', 'TC_Purchase_16', 'Validate Input Field Validation for Milk Product', NULL, '1. Select \"Milk Product\" from the Product Selection field. \n 2. Leave one or more fields (e.g., Milk Can Weight or Milk Can Type) empty. \n 3. Tap the \"Save\" button.', 'The app should display a validation error for the empty fields (e.g., \"Please fill in all required fields\").', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page with Milk Product selected.', NULL, 0, NULL),
('NTC_000000062', 'TC_Purchase_17', 'Validate Input Field Validation for Grocery Product', NULL, '1. Select \"Grocery Product\" from the Product Selection field. \n 2. Leave one or more fields (e.g., Product Weight, Purchase Rate per KG) empty. \n 3. Tap the \"Save\" button.', 'The app should display a validation error for the empty fields (e.g., \"Please fill in all required fields\").', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page with Grocery Product selected.', NULL, 0, NULL),
('NTC_000000063', 'TC_Purchase_18', 'Validate Layout Adaptation on Different Screen Sizes', NULL, '1. Open the app on different screen sizes (e.g., tablet, smartphone).', 'The layout should adapt, showing the correct fields for either Milk or Grocery products, and the buttons should be properly aligned.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page.', NULL, 0, NULL),
('NTC_000000064', 'TC_Purchase_19', 'Validate \"Summary\" and \"History\" Buttons Visibility', NULL, '1. Check the visibility of the \"Summary\" and \"History\" buttons at the bottom of the page.', 'Both buttons (\"Summary\" and \"History\") should be visible and clickable.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase Activity page.', NULL, 0, NULL),
('NTC_000000065', 'TC_PurchaseHistory_01', 'Validate Display of Purchase Date and Session Details', NULL, '1. Open the app and navigate to the Purchase History page.', 'The purchase date and session details should be displayed correctly.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page.', NULL, 0, NULL),
('NTC_000000066', 'TC_PurchaseHistory_02', 'Validate Display of Product Details for Each Purchase', NULL, '1. Navigate to the Purchase History page. \n 2. Check the product details listed under each purchase record (e.g., product name, quantity, price).', 'The product details should be listed correctly for each purchase, showing the product name, quantity, and price.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page with purchase records.', NULL, 0, NULL),
('NTC_000000067', 'TC_PurchaseHistory_03', 'Validate Navigation to Purchase Activity from Add Button', NULL, '1. Tap the \"Add\" button at the bottom of the page.', 'The app should navigate to the Purchase Activity page, where the user can add new products.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page.', NULL, 0, NULL),
('NTC_000000068', 'TC_PurchaseHistory_04', 'Validate Product Count Display for Each Purchase', NULL, '1. Navigate to the Purchase History page. \n 2. Check the number of products displayed for each purchase record.', 'The number of products purchased in each session should be displayed correctly.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page.', NULL, 0, NULL),
('NTC_000000069', 'TC_PurchaseHistory_05', 'Validate No Purchase History Message (If No Purchases Exist)', NULL, '1. Navigate to the Purchase History page with no prior purchases.', 'A message like \"No purchase history available\" should be displayed when there are no purchase records.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page and has not made any purchases.', NULL, 0, NULL),
('NTC_000000070', 'TC_PurchaseHistory_06', 'Validate Correct Session Details Display', NULL, '1. Navigate to the Purchase History page. \n 2. Check that the session details (e.g., session ID or session time) are displayed for each purchase.', 'The session details should be correctly displayed for each purchase record.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page.', NULL, 0, NULL),
('NTC_000000071', 'TC_PurchaseHistory_07', 'Validate Layout and Display on Different Screen Sizes', NULL, '1. Open the app on different screen sizes (e.g., tablet, smartphone).', 'The layout of the Purchase History page should adapt correctly to different screen sizes without breaking.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page.', NULL, 0, NULL),
('NTC_000000072', 'TC_PurchaseHistory_08', 'Validate Date and Session Information Accuracy', NULL, '1. Navigate to the Purchase History page. \n 2. Check that the displayed purchase date and session details match the actual purchase records.', 'The date and session information should accurately match the actual purchase details from the records.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User has made multiple purchases.', NULL, 0, NULL),
('NTC_000000073', 'TC_PurchaseHistory_09', 'Validate Data Persistence After Navigating Away', NULL, '1. Navigate to the Purchase History page. \n 2. Go to a different page (e.g., the Purchase Activity). \n 3. Navigate back to the Purchase History page.', 'The displayed purchase history should persist when returning to the Purchase History page.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History page.', NULL, 0, NULL),
('NTC_000000074', 'TC_PurchaseHistory_10', 'Validate No Product Details If No Products Were Purchased', NULL, '1. Navigate to the Purchase History page where there was no product purchase in a session.', 'There should be no product details displayed for that session, or it should show \"No products purchased.\"', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page and no products were purchased in a session.', NULL, 0, NULL),
('NTC_000000075', 'TC_PurchaseHistory_11', 'Validate Data Update After New Purchase', NULL, '1. Add new products via the \"Add\" button. \n 2. Go back to the Purchase History page.', 'The Purchase History page should update to include the new products and session details.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History page and adds new products.', NULL, 0, NULL),
('NTC_000000076', 'TC_PurchaseHistory_12', 'Validate Sorting of Purchase History by Date', NULL, '1. Navigate to the Purchase History page with multiple purchases. \n 2. Check that the purchase history is sorted by date (either ascending or descending).', 'The purchase history should be sorted correctly by date, with the most recent purchase appearing at the top (or bottom).', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User has made multiple purchases.', NULL, 0, NULL),
('NTC_000000077', 'TC_PurchaseHistory_13', 'Validate Product Details Displayed After Tapping on a Specific Purchase', NULL, '1. Tap on a specific purchase record.', 'The product details for that specific purchase session should be displayed correctly.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page with multiple purchases.', NULL, 0, NULL),
('NTC_000000078', 'TC_PurchaseHistory_14', 'Validate Add Button Visibility', NULL, '1. Navigate to the Purchase History page.', 'The \"Add\" button should be visible at the bottom of the page.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page.', NULL, 0, NULL),
('NTC_000000079', 'TC_PurchaseHistory_15', 'Validate Add Button Disabled When No Products Can Be Added', NULL, '1. Ensure the user cannot add products (e.g., the app is in read-only mode).', 'The \"Add\" button should be disabled or not clickable in this scenario.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page.', NULL, 0, NULL),
('NTC_000000080', 'TC_PurchaseHistory_16', 'Validate Add Button Functionality with Pending Data', NULL, '1. Navigate to the Purchase History page with pending purchase data. \n 2. Tap the \"Add\" button.', 'The app should navigate to the Purchase Activity page and allow the user to add products.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is on the Purchase History Activity page and has pending data in the Purchase Activity.', NULL, 0, NULL),
('NTC_000000081', 'TC_PurchaseSummary_01', 'Display product list', NULL, 'Add multiple products to the purchase list.', 'All products added should display correctly in the product list.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Products exist in the database.', NULL, 0, NULL),
('NTC_000000082', 'TC_PurchaseSummary_02', 'Validate product purchase count', NULL, 'Add products with specific quantities.', 'Products should display with their respective purchase counts.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Products exist in the database.', NULL, 0, NULL),
('NTC_000000083', 'TC_PurchaseSummary_03', 'Handle empty product list', NULL, 'Leave the product list empty and proceed.', 'System should display a message: \"No products to purchase.\"', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'No products selected.', NULL, 0, NULL),
('NTC_000000084', 'TC_PurchaseSummary_04', 'Merge duplicate products', NULL, 'Add duplicate products with different counts (e.g., Apple).', 'System should merge duplicates and display a combined count.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Products exist in the database.', NULL, 0, NULL),
('NTC_000000085', 'TC_PurchaseSummary_05', 'Validate total purchase count', NULL, 'Add multiple products with quantities.', 'The system should calculate and display the total purchase count.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Products exist in the database.', NULL, 0, NULL),
('NTC_000000086', 'TC_PurchaseSummary_06', 'Display updated list after removal', NULL, 'Remove a product from the list.', 'Product list should update to exclude the removed product.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Products added to the list.', NULL, 0, NULL),
('NTC_000000087', 'TC_PurchaseSummary_07', 'Handle invalid product input', NULL, 'Add a product with invalid details (e.g., negative count).', 'System should show an error message or reject invalid inputs.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User enters invalid product details.', NULL, 0, NULL),
('NTC_000000088', 'TC_PurchaseSummary_08', 'Handle large purchase counts', NULL, 'Add a product with a large purchase count (e.g., 10,000).', 'System should handle and display the count without errors.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Products exist in the database.', NULL, 0, NULL),
('NTC_000000089', 'TC_PurchaseSummary_09', 'Verify summary totals', NULL, 'Complete the activity and view the summary.', 'Summary should display all products, counts, and a correct total.', NULL, 'Procura', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Products exist in the database.', NULL, 0, NULL),
('NTC_000000090', 'TC_Login_01', 'Validate Empty Mobile Number, Email, and Password', NULL, '1. Leave Mobile Number, Mail ID, and Password fields empty. \n 2. Tap the Login button.', 'Error message for each field indicating they cannot be empty (e.g., \"Mobile Number cannot be empty\", \"Mail ID cannot be empty\", \"Password cannot be empty\").', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000091', 'TC_Login_02', 'Validate Empty Mobile Number with Filled Email and Password', NULL, '1. Leave Mobile Number field empty. \n 2. Enter valid Mail ID and Password. \n 3. Tap the Login button.', 'Error message for Mobile Number (e.g., \"Mobile Number cannot be empty\").', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000092', 'TC_Login_03', 'Validate Empty Mail ID with Filled Mobile Number and Password', NULL, '1. Leave Mail ID field empty. \n 2. Enter valid Mobile Number and Password. \n 3. Tap the Login button.', 'Error message for Mail ID (e.g., \"Mail ID cannot be empty\").', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000093', 'TC_Login_04', 'Validate Empty Password with Filled Mobile Number and Mail ID', NULL, '1. Leave Password field empty. \n 2. Enter valid Mobile Number and Mail ID. \n 3. Tap the Login button.', 'Error message for Password (e.g., \"Password cannot be empty\").', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000094', 'TC_Login_05', 'Validate Invalid Mobile Number Format', NULL, '1. Enter an invalid Mobile Number (e.g., too short or too long). \n 2. Enter valid Mail ID and Password. \n 3. Tap the Login button.', 'Error message for invalid Mobile Number (e.g., \"Invalid mobile number format\").', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000095', 'TC_Login_06', 'Validate Invalid Mail ID Format', NULL, '1. Enter valid Mobile Number. \n 2. Enter invalid Mail ID (e.g., \"invalidemail.com\"). \n 3. Enter valid Password. \n 4. Tap the Login button.', 'Error message for invalid Mail ID format (e.g., \"Invalid email format\").', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000096', 'TC_Login_07', 'Validate Invalid Password', NULL, '1. Enter valid Mobile Number or Mail ID. \n 2. Enter invalid Password. \n 3. Tap the Login button.', 'Error message indicating incorrect Password (e.g., \"Incorrect password\").', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000097', 'TC_Login_08', 'Validate Successful Login', NULL, '1. Enter valid Mobile Number (or Mail ID). \n 2. Enter correct Password. \n 3. Tap the Login button.', 'User is logged in successfully and redirected to the home screen/dashboard.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000098', 'TC_Login_09', 'Validate Login with Mobile Number and Password', NULL, '1. Select Mobile Number option from Mail ID selection TextView. \n 2. Enter valid Mobile Number. \n 3. Enter valid Password. \n 4. Tap the Login button.', 'User is logged in successfully and redirected to the home screen/dashboard.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000099', 'TC_Login_10', 'Validate Login with Mail ID and Password', NULL, '1. Select Mail ID option from Mail ID selection TextView. \n 2. Enter valid Mail ID. \n 3. Enter valid Password. \n 4. Tap the Login button.', 'User is logged in successfully and redirected to the home screen/dashboard.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000100', 'TC_Login_11', 'Validate \'Forgot Password\' Link (If Applicable)', NULL, '1. Tap the \"Forgot Password\" link.', 'App navigates to password reset screen where the user can request a password reset.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000101', 'TC_Login_12', 'Validate \'Login\' Button Disabled Initially', NULL, '1. Do not enter any details in the Mobile Number, Mail ID, and Password fields.', 'Login button is disabled until valid input is provided.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000102', 'TC_Login_13', 'Validate \'Login\' Button Enabled after Valid Input', NULL, '1. Enter valid Mobile Number (or Mail ID). \n 2. Enter valid Password.', 'Login button becomes enabled and allows the user to tap it.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App is launched, login page is displayed', NULL, 0, NULL),
('NTC_000000103', 'TC_Login_14', 'Validate Login Behavior with Social Media (If Applicable)', NULL, '1. Tap login option for social media account (e.g., Google). \n 2. Complete authentication process.', 'User is logged in using social media account and redirected to the home page.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'App has social media login options', NULL, 0, NULL),
('NTC_000000104', 'TC_Login_15', 'Validate Session Expiry After Logout', NULL, '1. Remain idle for a specified time (e.g., 15 minutes). \n 2. Attempt to perform any action.', 'User is logged out automatically and redirected to login screen.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'User is logged in, session is active', NULL, 0, NULL),
('NTC_000000105', 'TC_Login_16', 'Verify the pre-filled saved password functionality on the login page.', NULL, 'Observe the username and password fields.\nAttempt to modify the password field.\nClick on the \"Log In\" button without changing the fields.\nVerify successful login using the saved credentials.', 'The password field is pre-filled with the saved password in a masked format (******** or ••••••••).\nThe user is successfully logged in when clicking \"Log In\" without modifying the credentials.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'A valid username and password are already saved for the login page in the browser or application', NULL, 0, NULL),
('NTC_000000106', 'TC_QCApp_Home_01', 'Verify purchased product list display', NULL, 'Launch the app and view the home page.', 'RecyclerView should display the list of purchased products correctly.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Products exist in the database.', NULL, 0, NULL),
('NTC_000000107', 'TC_QCApp_Home_02', 'Handle empty product list', NULL, 'Launch the app with no purchased products.', 'The app should display an appropriate message, e.g., \"No products available.\"', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'No products are available.', NULL, 0, NULL),
('NTC_000000108', 'TC_QCApp_Home_03', 'Display milk product layout', NULL, 'Launch the app and view the RecyclerView.', 'Milk products should display with supplier details, milk type, qty, can type, and sample box number.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Milk products exist in the database.', NULL, 0, NULL),
('NTC_000000109', 'TC_QCApp_Home_04', 'Display non-milk product layout', NULL, 'Launch the app and view the RecyclerView.', 'Non-milk products should display with supplier details, product name, qty, rate, and price.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Non-milk products exist in the database.', NULL, 0, NULL),
('NTC_000000110', 'TC_QCApp_Home_05', 'Edit milk product details', NULL, 'Input values for milk degree, fat, snf, water content, and clr in the respective EditText fields.', 'Entered values should be accepted and editable fields should save changes locally.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Milk products exist in the database.', NULL, 0, NULL),
('NTC_000000111', 'TC_QCApp_Home_06', 'Save milk product details', NULL, 'Fill in all EditText fields for milk products and click the \"Save\" button.', 'Milk product details should be saved successfully and persist on the next load.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Milk products exist in the database.', NULL, 0, NULL),
('NTC_000000112', 'TC_QCApp_Home_07', 'Capture image for non-milk product', NULL, 'Tap the camera icon in the RecyclerView for a non-milk product, take a picture, and confirm.', 'Captured image should display and be associated with the respective non-milk product.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Non-milk products exist in the database.', NULL, 0, NULL),
('NTC_000000113', 'TC_QCApp_Home_08', 'Save image for non-milk product', NULL, 'After taking a picture, ensure the image is saved.', 'The saved image should persist and reload on the next view.', NULL, 'QualityControl', 'V1.0', '2025-03-25 09:50:00', '', '', '', 0, '', '0000-00-00 00:00:00', '', NULL, 'Non-milk products exist in the database.', NULL, 0, NULL);
